mapper.select:
select
id, created_at, created_by, updated_at, updated_by, deleted_at, sort, stable, status, memo, plugin_sort,
plugin_code, plugin_name, plugin_file
from sys_gen_plugin_list


        and id = #{id}


        and created_at = #{createdAt}


        and created_by = #{createdBy}


        and updated_at = #{updatedAt}


        and updated_by = #{updatedBy}


        and deleted_at = #{deletedAt}


        and sort = #{sort}


        and stable = #{stable}


        and status = #{status}


        and memo = #{memo}


        and plugin_sort = #{pluginSort}


        and plugin_code = #{pluginCode}


        and plugin_name = #{pluginName}


        and plugin_file = #{pluginFile}

select count(*)
from sys_gen_plugin_list


        and id = #{id}


        and created_at = #{createdAt}


        and created_by = #{createdBy}


        and updated_at = #{updatedAt}


        and updated_by = #{updatedBy}


        and deleted_at = #{deletedAt}


        and sort = #{sort}


        and stable = #{stable}


        and status = #{status}


        and memo = #{memo}


        and plugin_sort = #{pluginSort}


        and plugin_code = #{pluginCode}


        and plugin_name = #{pluginName}


        and plugin_file = #{pluginFile}
