mapper.select:
select
id, created_at, created_by, updated_at, updated_by, deleted_at, sort, stable, status, memo, parent_code, name,
path, component, redirect, title, icon, hidden, keep_alive, is_link
from sys_menu


        and id = #{id}


        and created_at = #{createdAt}


        and created_by = #{createdBy}


        and updated_at = #{updatedAt}


        and updated_by = #{updatedBy}


        and deleted_at = #{deletedAt}


        and sort = #{sort}


        and stable = #{stable}


        and status = #{status}


        and memo = #{memo}


        and parent_code = #{parentCode}


        and name = #{name}


        and path = #{path}


        and component = #{component}


        and redirect = #{redirect}


        and title = #{title}


        and icon = #{icon}


        and hidden = #{hidden}


        and keep_alive = #{keepAlive}


        and is_link = #{isLink}

select count(*)
from sys_menu


        and id = #{id}


        and created_at = #{createdAt}


        and created_by = #{createdBy}


        and updated_at = #{updatedAt}


        and updated_by = #{updatedBy}


        and deleted_at = #{deletedAt}


        and sort = #{sort}


        and stable = #{stable}


        and status = #{status}


        and memo = #{memo}


        and parent_code = #{parentCode}


        and name = #{name}


        and path = #{path}


        and component = #{component}


        and redirect = #{redirect}


        and title = #{title}


        and icon = #{icon}


        and hidden = #{hidden}


        and keep_alive = #{keepAlive}


        and is_link = #{isLink}
