mapper.select:
select
id, uid, address, content, like, attention, follower, username, avatar, level, homeLink, created_at, created_by,
updated_at, updated_by, deleted_at
from video_comments_user_info


        and id = #{id}


        and uid = #{uid}


        and address = #{address}


        and content = #{content}


        and like = #{like}


        and attention = #{attention}


        and follower = #{follower}


        and username = #{username}


        and avatar = #{avatar}


        and level = #{level}


        and homeLink = #{homelink}


        and created_at = #{createdAt}


        and created_by = #{createdBy}


        and updated_at = #{updatedAt}


        and updated_by = #{updatedBy}


        and deleted_at = #{deletedAt}

select count(*)
from video_comments_user_info


        and id = #{id}


        and uid = #{uid}


        and address = #{address}


        and content = #{content}


        and like = #{like}


        and attention = #{attention}


        and follower = #{follower}


        and username = #{username}


        and avatar = #{avatar}


        and level = #{level}


        and homeLink = #{homelink}


        and created_at = #{createdAt}


        and created_by = #{createdBy}


        and updated_at = #{updatedAt}


        and updated_by = #{updatedBy}


        and deleted_at = #{deletedAt}

mapper.update:
update video_comments_user_info

    `like`=`like`+1,
    likeIds=concat(likeIds,",",#{commentId})


    uid=#{uid}
;
update video_comments

    likes=likes+1


    id=#{commentId}

update video_comments_user_info


    `like`=`like`-1,
    likeIds=REPLACE(likeIds,#{r}, '')


    uid=#{uid}
;
update video_comments

    likes=likes-1


    id=#{commentId}
